import React , {useEffect, useState} from 'react';
import './style.css';
import Button from '../../components/Button';

function UserRegisterPage() {
	const [inputs, setInputs] = useState({});
	const [data, setData] = useState(null);
	const [error, setError] = useState(null);
	const [err] = useState(null);
	const handleChange = (event) => {
		const name = event.target.name;
		const value = event.target.value;

		setInputs(values => ({...values, [name]: value}))
	}

	// http://localhost:3001/api

	useEffect(() => {
		fetch('http://localhost:3001/api')
		.then(response => {
			if(response.ok){
				return response.json
			}
			throw response;
		}).then(data=> {
			setData(data)
		}).catch(error => {
			console.error("error fetcging data: ",error)
			setError(error)
		})
		.finally(() => {
			setLoading(false);
		})
	}, [])
	const handleSubmit = (event) => {
		event.preventDefault();

		//TODO: Add AJAX Request here
		console.log(inputs)


	  }
  	return (
		<div className='container'>
			<form onSubmit={handleSubmit}>
				<div className='row mb-5'>
					<div className='col-6'>
						<label className="d-flex justify-content-center align-items-center flex-column">
							Student Name:
							<input
								className="text-center"
								type="text"
								name="name"
								placeholder='Enter your name'
								onChange={handleChange}
							/>
						</label>
					</div>
					<div className='col-6'>
						<label className="d-flex justify-content-center align-items-center flex-column">
							Student ID:
							<input
								className="text-center"
								type="text"
								name="id"
								placeholder='Enter your HCT ID'
								onChange={handleChange}

							/>
						</label>
					</div>
				</div>
				<div className='row mb-5'>
					<div className='col-6'>
						<label className="d-flex justify-content-center align-items-center flex-column">
							Student Major:
							<input
								className="text-center"
								type="text"
								name="major"
								placeholder='Enter your Major'
								onChange={handleChange}

							/>
						</label>
					</div>
					<div className='col-6'>
						<label className="d-flex justify-content-center align-items-center flex-column">
							Student Year:
							<input
								className="text-center"
								type="text"
								name="year"
								placeholder='Enter your Year'
								onChange={handleChange}

							/>
						</label>
					</div>
				</div>
				<div className='row d-flex justify-content-center'>
					<div className='col-3'>
						<Button text="submit" className="w-25"></Button>
					</div>
				</div>
			</form>
		</div>
  	)
}

export default UserRegisterPage;
