import React, {useState} from 'react';
import './style.css';
import { PieChart } from "react-minimal-pie-chart";


function AdminDashboardPage() {
  let [weakCounter, setWeakCounter] = useState(0);
  let [fairCounter, setFairCounter] = useState(0);
  let [goodCounter, setGoodCounter] = useState(0);
  let [strongCounter, setStrongCounter] = useState(0);
  let [passwordCounter, setPasswordCounter] = useState(0);
  let [retrieved, setRetrieved] = useState(false);

  async function retrievePasswords() {
    setRetrieved(current => !current)
		const response = await fetch("http://localhost:3001/retrievePasswords", {
			method: 'GET',
			headers: {
				'Accept': 'application/json',
				'Content-Type': 'application/json'
			}
		});
		const data = await response.json();
    data.forEach((password) => {
      setPasswordCounter(passwordCounter + 1);
      switch(password.strength){
        case 'weak':
          weakCounter = weakCounter + 1
          setWeakCounter(weakCounter)
          console.log("weakCounter: ", weakCounter)

          break;
        case 'fair':
          fairCounter = fairCounter + 1

          setFairCounter(fairCounter)
          console.log("fairCounter: ", fairCounter)

          break;
        case 'good':
          goodCounter = goodCounter + 1;
          setGoodCounter(goodCounter)
          console.log("goodCounter: ", goodCounter)

          break;
        case 'strong':
          fairCounter = fairCounter + 1

          setStrongCounter(strongCounter + 1)
          console.log("strongCounter: ", strongCounter)

          break;

        default:
          console.log(password.strength)
      }
    })


	}

  if(!retrieved){
    retrievePasswords()
  }

  return (
    <div className='w-100'>
      <div className="row">
        <div className='col-4'>
          <div className="card">
            <div className="card-body">
              <div className="card-title text-center">Number of Passwords</div>
                <p className='card-text text-center'>{passwordCounter}</p>
            </div>
          </div>
        </div>
        <div className='col-4'>
          <div className="card">
            <div className="card-body">
              <div className="card-title text-center">Number of Passwords</div>
                <p className='card-text text-center'>{passwordCounter}</p>
            </div>
          </div>
        </div>
        <div className='col-4'>
          <div className="card">
            <div className="card-body">
              <div className="card-title text-center">Number of Passwords</div>
                <p className='card-text text-center'>{passwordCounter}</p>
            </div>
          </div>
        </div>
      </div>
      <div className='row'>
        <div className='col-6'>
        <div className="card">
            <div className="card-body">
              <div className="card-title text-center">Strength of Password</div>
              <PieChart
                animation
                animationDuration={500}
                animationEasing="ease-out"
                center={[50, 50]}
                data={[
                  {
                    color: '#01a917',
                    title: "strong",
                    value: strongCounter,
                  },
                  {
                    color: "#0b75ed",
                    title: "good",
                    value: goodCounter,
                  },
                  {
                    color: "#ebbd04",
                    title: "fair",
                    value: fairCounter,
                  },
                  {
                    color: "#e20b07",
                    title: "weak",
                    value: weakCounter,
                  },
                ]}
                labelPosition={50}
                lengthAngle={360}
                lineWidth={30}
                paddingAngle={0}
                radius={50}
                rounded
                startAngle={0}
                viewBoxSize={[100, 100]}
            />
            </div>
          </div>


        </div>

      </div>
      <div className="chart-container">

      </div>
    </div>
  )
}

export default AdminDashboardPage;
