import React, {useState} from "react";
import zxcvbnAsync from "zxcvbn-async";
import "./style.css";
const StrengthMeter = () => {
  	const [password, setPassword] = useState('');

	const handleChange = (event) => {
		// ðŸ‘‡ Get input value from "event"
		console.log(password)
		zxcvbnAsync.load({})
		.then((zxcvbn) => {
			var results = zxcvbn(password);
			console.log(results.crack_times_display.offline_slow_hashing_1e4_per_second)
		});
		setPassword(event.target.value);
	};
//   const pwdValidate = props.password;


  const initPwdChecker = () => {
    let pwdCheck = 0;
    let validateRegex = ["[A-Z]", "[a-z]", "[0-9]", "\\W"];
    validateRegex.forEach((regex) => {
      if (new RegExp(regex).test(password)) {
        pwdCheck += 1;
      }
    });
    switch (pwdCheck) {
      case 0:
        return {
          strength: 0,
          val: "",
          backgroundColor: ''
        };
      case 1:
        return {
          strength: 1,
          val: "weak",
          backgroundColor: '#e20b07'
        };
      case 2:
        return {
          strength: 2,
          val: "fair",
          backgroundColor: '#ebbd04'
        };
      case 3:
        return {
          strength: 3,
          val: "good",
          backgroundColor: '#0b75ed'
        };
      case 4:
        return {
          strength: 4,
          val: "strong",
          backgroundColor: '#01a917'
        };
      default:
        return null;
    }
  };



  	return (
		<div className='w-100 d-flex flex-column justify-content-center '>
			<div className='row'>
				<form>
					<div className='row mb-5'>
						<div className='col-12 d-flex justify-content-center align-items-center'>
							<input
								className=" w-50 text-center"
								style={{
									height:'5rem',
									fontSize:'20px',
									backgroundColor: `${initPwdChecker().backgroundColor}`

								}}
								value={password}
								type="text"
								name="passwordChecker"
								placeholder='Enter Your Password'
								onChange={handleChange}
							/>
						</div>

						<div className='col-12 d-flex justify-content-center align-items-center mt-3'>
							<div className="pwd-label">
								{password && (
									<div>
										<p
											className="text-uppercase fw-bold"
											style={{
												color: `${initPwdChecker().backgroundColor}`
											}}
										>
											{initPwdChecker().val}
										</p>
									</div>
								)}
							</div>
						</div>
						<div className='col-12 d-flex justify-content-center align-items-center mt-3'>
							<div className="pwd-label">
								{password && (
									<div>
										<p
											className="text-uppercase fw-bold"
											style={{
												color: `${initPwdChecker().backgroundColor}`
											}}
										>
											{initPwdChecker().val}
										</p>
									</div>
								)}
							</div>
						</div>
					</div>
				</form>
			</div>
		</div>
  	);
};
export default StrengthMeter;
