import React, {useState} from "react";
import "./style.css";
const StrengthMeter = (props) => {
  const [password, setPassword] = useState(null);

	const handleChange = (event) => {
	  // ðŸ‘‡ Get input value from "event"
	  setPassword(event.target.value);
	  console.log(password);
	};
  // const pwdValidate = props.password;
  const initPwdChecker = () => {
    let pwdCheck = 0;
    let validateRegex = ["[A-Z]", "[a-z]", "[0-9]", "\\W"];
    validateRegex.forEach((regex) => {
      if (new RegExp(regex).test(password)) {
        pwdCheck += 1;
      }
    });
    switch (pwdCheck) {
      case 0:
        return {
          strength: 0,
          val: "",
          backgroundColor: ''
        };
      case 1:
        return {
          strength: 1,
          val: "weak",
          backgroundColor: ''
        };
      case 2:
        return {
          strength: 2,
          val: "fair",
          backgroundColor: ''
        };
      case 3:
        return {
          strength: 3,
          val: "good",
          backgroundColor: 'blue'
        };
      case 4:
        return {
          strength: 4,
          val: "strong",
          backgroundColor: 'green'
        };
      default:
        return null;
    }
  };



  return (
    <>
      <div className="wrapper">
      <input
									className=" w-50 text-center"
									style={{
										height:'5rem',
										fontSize:'20px'
									}}
									value={password}
									type="text"
									name="passwordChecker"
									placeholder='Enter Your Password'
									onChange={handleChange}
								/>
        <progress
          className={`pwd-checker-bar strength-${initPwdChecker().val}`}
          value={initPwdChecker().strength}
          max="4"
        />
        <br />
        <p className="pwd-label">
          {props.password && (
            <div>
              <p className={`label strength-${initPwdChecker().val}`}>
                Password strength validation:
                <strong>{initPwdChecker().val} </strong>
              </p>
            </div>
          )}
        </p>
      </div>
    </>
  );
};
export default StrengthMeter;
