import React, {useState} from "react";
import "./style.css";
const StrengthMeter = () => {
  	const [password, setPassword] = useState('');

	const handleChange = (event) => {
		// ðŸ‘‡ Get input value from "event"
		setPassword(event.target.value);
	};
//   const pwdValidate = props.password;
  const initPwdChecker = () => {
    let pwdCheck = 0;
    let validateRegex = ["[A-Z]", "[a-z]", "[0-9]", "\\W"];
    validateRegex.forEach((regex) => {
      if (new RegExp(regex).test(password)) {
        pwdCheck += 1;
      }
    });
    switch (pwdCheck) {
      case 0:
        return {
          strength: 0,
          val: "",
          backgroundColor: ''
        };
      case 1:
        return {
          strength: 1,
          val: "weak",
          backgroundColor: ''
        };
      case 2:
        return {
          strength: 2,
          val: "fair",
          backgroundColor: ''
        };
      case 3:
        return {
          strength: 3,
          val: "good",
          backgroundColor: 'blue'
        };
      case 4:
        return {
          strength: 4,
          val: "strong",
          backgroundColor: 'green'
        };
      default:
        return null;
    }
  };



  	return (
		<div className='w-100 d-flex flex-column justify-content-center '>
			<div className='row'>
				<form>
					<div className='row mb-5'>
						<div className='col-12 d-flex justify-content-center align-items-center'>
							<input
								className=" w-50 text-center"
								style={{
									height:'5rem',
									fontSize:'20px',
									backgr
								}}
								value={password}
								type="text"
								name="passwordChecker"
								placeholder='Enter Your Password'
								onChange={handleChange}
							/>
						</div>

						<div className='col-12 d-flex justify-content-center align-items-center'>
							<div className="wrapper">
								<progress
									className={`pwd-checker-bar strength-${initPwdChecker().val}`}
									value={initPwdChecker().strength}
									max="4"
								/>
								<p className="pwd-label">
									{password && (
									<div>
										<p className={`label strength-${initPwdChecker().val}`}>
											Password strength validation:
											<strong>{initPwdChecker().val} </strong>
										</p>
									</div>
									)}
								</p>
							</div>
						</div>
					</div>
				</form>
			</div>
		</div>
  	);
};
export default StrengthMeter;
