import React, {useState} from 'react';
import './style.css';
import { PieChart } from "react-minimal-pie-chart";


function AdminDashboardPage() {
  const [weakCounter, setWeakCounter] = useState(0);
  const [fairCounter, setFairCounter] = useState(0);
  const [goodCounter, setGoodCounter] = useState(0);
  const [strongCounter, setStrongCounter] = useState(0);
  const [passwordCounter, setPasswordCounter] = useState(0);
  const [retrieved, setRetrieved] = useState(false);

  async function retrievePasswords() {
    setRetrieved(current => !current)
		const response = await fetch("http://localhost:3001/retrievePasswords", {
			method: 'GET',
			headers: {
				'Accept': 'application/json',
				'Content-Type': 'application/json'
			}
		});
		const data = await response.json();
    data.forEach((password) => {
      setPasswordCounter(passwordCounter + 1);
      switch(password.strength){
        case 'weak':
          setWeakCounter(weakCounter + 1)
          break;
        case 'fair':
          setFairCounter(fairCounter + 1)
          break;
        case 'good':
          setGoodCounter(goodCounter + 1)
          break;
        case 'strong':
          setStrongCounter(strongCounter + 1)
          break;

        default:
          console.log(password.strength)
      }
    })
	}

  if(!retrieved){
    retrievePasswords()
  }

  return (
    <div>
      <div className="row">
        <div className='col-4'>
          <div className="card">
            <div className="card-header">
              Quote
            </div>
            <div className="card-body">
              <blockquote className="blockquote mb-0">
                <p>{}</p>
                <footer className="blockquote-footer">Someone famous in <cite title="Source Title">Source Title</cite></footer>
              </blockquote>
            </div>
          </div>
        </div>
      </div>
      <div className="chart-container">
        <PieChart
          animation
          animationDuration={500}
          animationEasing="ease-out"
          center={[50, 50]}
          data={[
            {
              color: '#01a917',
              title: "strong",
              value: strongCounter,
            },
            {
              color: "#0b75ed",
              title: "good",
              value: goodCounter,
            },
            {
              color: "#ebbd04",
              title: "fair",
              value: fairCounter,
            },
            {
              color: "#e20b07",
              title: "weak",
              value: weakCounter,
            },
          ]}
          labelPosition={50}
          lengthAngle={360}
          lineWidth={30}
          paddingAngle={0}
          radius={50}
          rounded
          startAngle={0}
          viewBoxSize={[100, 100]}
       />
      </div>
    </div>
  )
}

export default AdminDashboardPage;
