import React from 'react';

function Button(props) {
  console.log(props.textAlign)
  return (
    <button
      style={{
        backgroundColor: props.backgroundColor !== 'undefined' ? 'transparent' : props.backgroundColor,
        color: 'white',
        fontSize: '16px',
        padding: '15px 32px',
        margin: '4px 2px',
        cursor: 'pointer',
        textAlign: props.textAlign === 'center' ? 'center' : props.textAlign,
        textDecoration: 'none',
        border: 'black solid 2px',
        borderRadius: props.borderRadius === 'center' ? 'center' : props.textAlign
      }}
    >
      {props.text}
    </button>
  );
}

// Button.defaultProps = {
//   color: 'transparent',
//   text: 'center'
// }

export default Button;
