//     validateRegex.forEach((regex) => {
//       if (new RegExp(regex).test(password)) {
//         pwdCheck += 1;
//       }
//     });
//     switch (pwdCheck) {
//       case 0:
//         return {
//           strength: 0,
//           val: "",
//           backgroundColor: ''
//         };
//       case 1:
//         return {
//           strength: 1,
//           val: "weak",
//           backgroundColor: '#e20b07'
//         };
//       case 2:
//         return {
//           strength: 2,
//           val: "fair",
//           backgroundColor: '#ebbd04'
//         };
//       case 3:
//         return {
//           strength: 3,
//           val: "good",
//           backgroundColor: '#0b75ed'
//         };
//       case 4:
//         return {
//           strength: 4,
//           val: "strong",
//           backgroundColor: '#01a917'
//         };
//       default:
//         return null;
//     }
//   };



  	return (
		<div className='w-100 d-flex flex-column justify-content-center '>
			<div className='row'>
				<form>
					<div className='row mb-5 d-flex flex-column justify-content-center'>
						<div className='col-12 d-flex justify-content-center align-items-center'>
							<input
								className=" w-50 text-center"
								style={{
									height:'5rem',
									fontSize:'20px',
									backgroundColor: `${passwordStrength.backgroundColor}`
								}}
								value={password}
								type="text"
								name="passwordChecker"
								placeholder='Enter Your Password'
								onChange={handleChange}
							/>
						</div>

						<div className='col-12 d-flex justify-content-center align-items-center mt-3'>
							<div className="pwd-label">
								{password && (
									<div>
										<p
											className="text-uppercase fw-bold"
											style={{
												// color: `${initPwdChecker().backgroundColor}`
											}}
										>
											{/* {initPwdChecker().val} */}
										</p>
									</div>
								)}
							</div>
						</div>
						<div className='col-12 d-flex text-center justify-content-center align-items-center mt-3'>
							{crackDuration &&
								`It would take a computer about ${crackDuration} to crack your password`
							}
						</div>
					</div>
				</form>
			</div>
		</div>
  	);
};
export default StrengthMeter;
