import React, {useState, useEffect} from 'react';
import './style.css';
import { PieChart } from "react-minimal-pie-chart";


function AdminDashboardPage() {
  const [weakCounter, setWeakCounter] = useState(0);
  const [fairCounter, setFairCounter] = useState(0);
  const [goodCounter, setGoodCounter] = useState(0);
  const [strongCounter, setStrongCounter] = useState(0);
  const [passwordCounter, setPasswordCounter] = useState(0);



  useEffect(() => {
    async function retrievePasswords() {
      var weak = 0;
      var fair = 0;
      var good = 0;
      var strong = 0;
      var passwordCounter = 0;

      const password_response = await fetch("http://localhost:3001/retrievePasswords", {
        method: 'GET',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });

      const student_response = await fetch("http://localhost:3001/retrieveStudents", {
        method: 'GET',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });

      const data = await password_response.json();

      
      data.forEach((password) => {
        passwordCounter++;
        switch(password.strength){
          case 'weak':
            weak++;
            break;
          case 'fair':
            fair++;
            break;
          case 'good':
            good++;
            break;
          case 'strong':
            strong++;
            break;

          default:
            console.log(password.strength)
        }
      })

      setWeakCounter(weak)
      setFairCounter(fair)
      setGoodCounter(good)
      setStrongCounter(strong)
      setPasswordCounter(passwordCounter)
    }
      retrievePasswords()
  });


  return (
    <div className='w-100'>
      <div className="row">
        <div className='col-4'>
          <div className="card">
            <div className="card-body">
              <div className="card-title text-center">Number of Passwords</div>
                <p className='card-text text-center'>{passwordCounter}</p>
            </div>
          </div>
        </div>
        <div className='col-4'>
          <div className="card">
            <div className="card-body">
              <div className="card-title text-center">Number of Passwords</div>
                <p className='card-text text-center'>{passwordCounter}</p>
            </div>
          </div>
        </div>
        <div className='col-4'>
          <div className="card">
            <div className="card-body">
              <div className="card-title text-center">Number of Passwords</div>
                <p className='card-text text-center'>{passwordCounter}</p>
            </div>
          </div>
        </div>
      </div>
      <div className='row'>
        <div className='col-6'>
        <div className="card">
            <div className="card-body">
              <div className="card-title text-center">Strength of Password</div>
              <PieChart
                animation
                animationDuration={500}
                animationEasing="ease-out"
                center={[50, 50]}
                data={[
                  {
                    color: '#01a917',
                    title: "strong",
                    value: strongCounter,
                  },
                  {
                    color: "#0b75ed",
                    title: "good",
                    value: goodCounter,
                  },
                  {
                    color: "#ebbd04",
                    title: "fair",
                    value: fairCounter,
                  },
                  {
                    color: "#e20b07",
                    title: "weak",
                    value: weakCounter,
                  },
                ]}
                labelPosition={50}
                lengthAngle={360}
                lineWidth={30}
                paddingAngle={0}
                radius={50}
                rounded
                startAngle={0}
                viewBoxSize={[100, 100]}
            />
            </div>
          </div>


        </div>

      </div>
      <div className="chart-container">

      </div>
    </div>
  )
}

export default AdminDashboardPage;
