import React, {useState} from 'react';
import './style.css';
import { PieChart } from "react-minimal-pie-chart";


function AdminDashboardPage() {
  const [weakCounter, setWeakCounter] = useState(0);
  const [fairCounter, setFairCounter] = useState(0);
  const [goodCounter, setGoodCounter] = useState(0);
  const [strongCounter, setStrongCounter] = useState(0);
  const [passwordCounter, setPasswordCounter] = useState(0);
  const [retrieved, setRetrieved] = useState(false);

  async function retrievePasswords() {
    setRetrieved(current => !current)
		const response = await fetch("http://localhost:3001/retrievePasswords", {
			method: 'GET',
			headers: {
				'Accept': 'application/json',
				'Content-Type': 'application/json'
			}
		});
		const data = await response.json();
    data.forEach((password) => {
      setPasswordCounter(passwordCounter + 1);
      switch(password.strength){
        case 'weak':
          setWeakCounter(weakCounter + 1)
          break;
        case 'fair':
          setFairCounter(fairCounter + 1)
          break;
        case 'good':
          setGoodCounter(goodCounter + 1)
          break;
        case 'strong':
          setStrongCounter(strongCounter + 1)
          break;

        default:
          console.log(password.strength)
      }
    })
	}

  if(!retrieved){
    retrievePasswords()
  }

  return (
    <div className='w-100'>
      <div className="row">
        <div className='col-6'>
          <div className="card">
            <div className="card-body">
              <div className="card-title">Number of Passwords</div>
                <p className='card-text'>{passwordCounter}</p>
            </div>
          </div>
        </div>
      </div>
      <div className='row'>
        <div className='col-6'></div>

      </div>
      <div className="chart-container">

      </div>
    </div>
  )
}

export default AdminDashboardPage;
